CREATE OR REPLACE FUNCTION obter_cor_classif_agenda (cd_medico_p bigint, ie_classif_agenda_p text, ie_tipo_cor_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_w		varchar(100);
ds_cor_fonte_w		varchar(10);
ds_cor_fundo_w		varchar(10);

/* F - Fonte
   U - Fundo */
BEGIN

select	max(ds_cor_fonte),
	max(ds_cor_fundo)
into STRICT	ds_cor_fonte_w,
	ds_cor_fundo_w
from	med_classif_agenda
where	cd_medico		= cd_medico_p
and	ie_classif_agenda	= ie_classif_agenda_p;

if (ie_tipo_cor_p = 'F') then
	ds_cor_w	:= ds_cor_fonte_w;
else
	ds_cor_w	:= ds_cor_fundo_w;
end if;

return ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
