CREATE OR REPLACE FUNCTION obter_cor_conta_etapa ( nr_seq_etapa_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_etapa_w	bigint;
ds_cor_etapa_w	varchar(15) := '0';


BEGIN

select	max(coalesce(ds_cor_etapa,0))
into STRICT	ds_cor_etapa_w
from	fatur_etapa
where	nr_sequencia	= nr_seq_etapa_p
and	coalesce(ie_situacao,'A') = 'A';

if (coalesce(ds_cor_etapa_w::text, '') = '') then
	ds_cor_etapa_w	:=	0;
end if;

return	ds_cor_etapa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
