SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_copia_rep (cd_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
C = Cor
M = Mensagem
S = Campo Marcado
*/
cd_grupo_material_w 	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
ds_material_w		varchar(255);
ds_cor_w		varchar(15);
ds_mensagem_w		varchar(255);
ie_marcado_w		varchar(1);
ds_retorno_w		varchar(255);

c01 CURSOR FOR
	SELECT	ds_cor,
		ds_mensagem,
		ie_marcado
	from	regra_cor_material_copia
	where	coalesce(cd_material,cd_material_p) 				= cd_material_p
	and	coalesce(cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w) 	= cd_subgrupo_material_w
	and	coalesce(cd_classe_material,cd_classe_material_w) 		= cd_classe_material_w
	and	coalesce(ie_situacao,'A')	= 'A'
	order by coalesce(cd_material,0),
		coalesce(cd_grupo_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_classe_material,0);

BEGIN

select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	substr(ds_material,1,240)
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w,
	ds_material_w
from 	estrutura_material_v
where 	cd_material		= cd_material_p;


open C01;
loop
fetch C01 into
	ds_cor_w,
	ds_mensagem_w,
	ie_marcado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_cor_w	:= ds_cor_w;
	ds_mensagem_w	:= ds_mensagem_w;
	ie_marcado_w	:= ie_marcado_w;
	end;
end loop;
close C01;


if (ie_opcao_p = 'C') then
	ds_retorno_w := ds_cor_w;
elsif (ie_opcao_p = 'M') then
	ds_retorno_w := ds_mensagem_w;
elsif (ie_opcao_p = 'S') then
	ds_retorno_w := ie_marcado_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
