CREATE OR REPLACE FUNCTION obter_cor_estagio_alt (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


  ds_cor_w varchar(250);

BEGIN
  select max(ds_cor)
  into STRICT ds_cor_w
  from alteracao_tasy_estagio b
  where b.nr_sequencia = (
	   	  SELECT max(a.nr_seq_estagio)
		  from alteracao_tasy_gestao a
		  where a.nr_seq_alteracao = nr_sequencia_p
		  and	(DT_LIBERACAO IS NOT NULL AND DT_LIBERACAO::text <> ''));


return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
