SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_estagio_pend (nr_Sequencia_p bigint) RETURNS bigint AS $body$
DECLARE



return_w			bigint;
nm_usuario_desvinc_w		varchar(15);
dt_fechamento_w			timestamp;
nr_seq_estagio_w		bigint;
ie_tipo_estagio_w		varchar(15);

C01 CURSOR FOR
	SELECT	nm_usuario_desvinc,
		dt_fechamento,
		nr_seq_estagio
	from	cta_pendencia
	where	nr_sequencia = nr_Sequencia_p;




BEGIN

open C01;
loop
fetch C01 into
	nm_usuario_desvinc_w,
	dt_fechamento_w,
	nr_seq_estagio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (nm_usuario_desvinc_w IS NOT NULL AND nm_usuario_desvinc_w::text <> '') then
		return_w:= 2835;
	elsif (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') then
		return_w:= 2834;
	else

		select	coalesce(max(ie_tipo_estagio),'EA')
		into STRICT	ie_tipo_estagio_w
		from	cta_estagio_pend
		where	nr_sequencia = nr_seq_estagio_w;

		if (ie_tipo_estagio_w = 'EA') then
			return_w:= 2832;
		elsif (ie_tipo_estagio_w = 'A') then
			return_w:= 2831;
		elsif (ie_tipo_estagio_w = 'F') then
			return_w:= 2834;
		end if;

	end if;

	end;
end loop;
close C01;

return	return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
