CREATE OR REPLACE FUNCTION obter_cor_etapa_viagem ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


/* COR 1 (631) - Viagem Prevista
   COR 2 (632) - Viagem Confirmada
   COR 3 (633) - Viagem Autorizada
   COR 4 (634) - Em Viagem
   COR 5 (635) - Elaboração Relatório de Viagem
   COR 6 (636) - Relatório Aprovado
   COR 7 (637) - Finalizada
   COR 8 (1218)- Cancelada
   COR 9 (4851)- Reprovada
*/
nr_seq_cor_w		bigint;
ie_etapa_viagem_w	varchar(1);



BEGIN


select 	max(ie_etapa_viagem)
into STRICT	ie_etapa_viagem_w
from 	via_viagem
where 	nr_sequencia = nr_sequencia_p;

if	ie_etapa_viagem_w = '0' then
	nr_seq_cor_w := 631;
elsif	ie_etapa_viagem_w = '1' then
	nr_seq_cor_w := 632;
elsif	ie_etapa_viagem_w = '2' then
	nr_seq_cor_w := 633;
elsif	ie_etapa_viagem_w = '3' then
	nr_seq_cor_w := 634;
elsif	ie_etapa_viagem_w = '4' then
	nr_seq_cor_w := 635;
elsif	ie_etapa_viagem_w = '5' then
	nr_seq_cor_w := 636;
elsif	ie_etapa_viagem_w = '6' then
	nr_seq_cor_w := 637;
elsif	ie_etapa_viagem_w = '7' then
	nr_seq_cor_w := 1075;
elsif	ie_etapa_viagem_w = '8' then
	nr_seq_cor_w := 1214;
elsif	ie_etapa_viagem_w = '9' then
	nr_seq_cor_w := 4851;
end if;


RETURN nr_seq_cor_w;
END;
