CREATE OR REPLACE FUNCTION obter_cor_etapa ( nr_interno_conta_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_etapa_w	bigint;
ds_cor_etapa_w	varchar(15) := '0';


BEGIN

select	somente_numero(obter_ultima_etapa(nr_interno_conta_p,'ET'))
into STRICT	nr_seq_etapa_w
;

if (nr_seq_etapa_w IS NOT NULL AND nr_seq_etapa_w::text <> '') and (nr_seq_etapa_w	> 0) then
	select	max(coalesce(ds_cor_etapa,0))
	into STRICT	ds_cor_etapa_w
	from	fatur_etapa
	where	nr_sequencia	= nr_seq_etapa_w
	and	coalesce(ie_situacao,'A') = 'A';
end if;

if (coalesce(ds_cor_etapa_w::text, '') = '') then
	ds_cor_etapa_w	:=	0;
end if;

return	ds_cor_etapa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
