SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_exame_grid ( ds_cor_p text, nr_coluna_p bigint) RETURNS bigint AS $body$
DECLARE


lista_w	dbms_sql.varchar2_table;
ds_retorno_w	bigint;
ds_linha_w	varchar(255);
BEGIN

if (ds_cor_p IS NOT NULL AND ds_cor_p::text <> '') then

	lista_w	:= obter_lista_string(ds_cor_p,',');

	if (lista_w.count	>0) then

		for i in lista_w.first..lista_w.last loop
			begin
			ds_linha_w	:= lista_w(i);

			if (substr(ds_linha_w,1,2)	= nr_coluna_p) then
				ds_retorno_w	:= somente_numero(substr(ds_linha_w,3));
			end if;

			end;
		end loop;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
