CREATE OR REPLACE FUNCTION obter_cor_fonte_tcr ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cor_w	varchar(15);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	ds_cor_fonte
	into STRICT	ds_cor_w
	from	triagem_classif_risco
	where	nr_sequencia	= nr_sequencia_p;

end if;

return ds_cor_w;

END;
