SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_gedipa_html (dt_gedipa_p timestamp, cd_setor_atendimento_p setor_atendimento.cd_setor_atendimento%type default null) RETURNS bigint AS $body$
DECLARE


qt_minuto_w	bigint;
nr_sequencia_w	gedipa_regra_cor.nr_sequencia%type;
ie_gedipa_fracionado_w	funcao_param_perfil.vl_parametro%type;

C01 CURSOR FOR
SELECT 	nr_sequencia
from	gedipa_regra_cor
where 	qt_minuto_w >= qt_minutos_regra
and	qt_minuto_w <= qt_minutos_max_rega
and	((cd_setor_atendimento_p = cd_setor_atendimento or coalesce(cd_setor_atendimento::text, '') = '')
	or ie_gedipa_fracionado_w = 'S')
order by cd_setor_atendimento desc;

BEGIN
ie_gedipa_fracionado_w := obter_param_usuario(3112, 63, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, obter_estabelecimento_ativo, ie_gedipa_fracionado_w);

select	obter_min_entre_datas(clock_timestamp(),dt_gedipa_p,1)
into STRICT	qt_minuto_w
;

for	c01_w in c01 loop
	begin
		if (c01_w.nr_sequencia IS NOT NULL AND c01_w.nr_sequencia::text <> '') then
			nr_sequencia_w	:= c01_w.nr_sequencia;
		end if;
	end;
end loop;

return	coalesce(nr_sequencia_w, 0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
