CREATE OR REPLACE FUNCTION obter_cor_gedipa (dt_gedipa_p timestamp) RETURNS varchar AS $body$
DECLARE

qt_minuto_w	bigint;
ds_cor_w	varchar(15);

BEGIN

select	obter_min_entre_datas(clock_timestamp(),dt_gedipa_p,1)
into STRICT	qt_minuto_w
;

select 	coalesce(max(ds_cor),'clWhite')
into STRICT 	ds_cor_w
from	gedipa_regra_cor
where 	qt_minuto_w >= qt_minutos_regra 
and	qt_minuto_w <= qt_minutos_max_rega;

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
