SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_grace_ii (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/*
Descrição 			ie_opcao_p

Risco de 1 ano			R1
Risco de 3 anos			R3
Risco de 1 ano MI		RMI


*/
ds_retorno_w varchar(255) := '';

QT_PONTUACAO_1_w			double precision;
QT_PONTUACAO_3_w			double precision;
QT_PONTUACAO_6_w			double precision;
QT_PONTUACAO_MI_w			double precision;


BEGIN

Select 	max(QT_PONTUACAO_1),
		max(QT_PONTUACAO_3),
		max(QT_PONTUACAO_6),
		max(QT_PONTUACAO_MI)
into STRICT	QT_PONTUACAO_1_w,
		QT_PONTUACAO_3_w,
		QT_PONTUACAO_6_w,
		QT_PONTUACAO_MI_w
from	escala_grace_ii
where	nr_sequencia = nr_sequencia_p;


if (ie_opcao_p = 'R1') then

	if (QT_PONTUACAO_1_w < QT_PONTUACAO_6_w) then

		QT_PONTUACAO_1_w := Arredondondar_Grace_II(QT_PONTUACAO_1_w + ((QT_PONTUACAO_6_w - QT_PONTUACAO_1_w) / 2), 1);

	end if;

	if (QT_PONTUACAO_1_w < 4) then

		ds_retorno_w := 'green';

	elsif (QT_PONTUACAO_1_w <= 12) then

		ds_retorno_w := 'yellow';

	else

		ds_retorno_w := 'red';

	end if;

elsif (ie_opcao_p = 'R3') then

	if (QT_PONTUACAO_3_w < 12) then

		ds_retorno_w := 'green';

	elsif (QT_PONTUACAO_3_w <= 30) then

		ds_retorno_w := 'yellow';

	else

		ds_retorno_w := 'red';

	end if;

elsif (ie_opcao_p = 'RMI') then

	if (QT_PONTUACAO_1_w < QT_PONTUACAO_6_w) then

		QT_PONTUACAO_1_w := Arredondondar_Grace_II(QT_PONTUACAO_1_w + ((QT_PONTUACAO_6_w - QT_PONTUACAO_1_w) / 2), 1);

	end if;

	if (QT_PONTUACAO_1_w >= QT_PONTUACAO_MI_w) then

		QT_PONTUACAO_MI_w := QT_PONTUACAO_1_w;

	end if;

	if (QT_PONTUACAO_MI_w < 10) then

		ds_retorno_w := 'green';

	elsif (QT_PONTUACAO_MI_w <= 22) then

		ds_retorno_w := 'yellow';

	else

		ds_retorno_w := 'red';

	end if;

end if;


return 	ds_retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
