CREATE OR REPLACE FUNCTION obter_cor_grafico_dispositivo (nr_seq_dispositivo_p bigint, ie_html_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_cor_grafico_w		varchar(15)	:= null;
ds_cor_grafico_html_w	varchar(15)	:= null;
ds_cor_dark_mode_w 		dispositivo.ds_cor_grafico_escuro%type := null;
dark_mode_w 			funcao_parametro.vl_parametro_padrao%type;


BEGIN

dark_mode_w := obter_param_usuario(872, 546, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, dark_mode_w);

select	coalesce(ds_cor_grafico,'clBlack'),
		coalesce(ds_cor_html,'#000'),
		coalesce(ds_cor_grafico_escuro,'#000')
into STRICT	ds_cor_grafico_w,
		ds_cor_grafico_html_w,
		ds_cor_dark_mode_w
from	dispositivo
where	nr_sequencia = nr_seq_dispositivo_p;

if (ie_html_p = 'S') then
  if (dark_mode_w = 'S') then
    ds_cor_grafico_w:= ds_cor_dark_mode_w;
  else
    ds_cor_grafico_w :=  ds_cor_grafico_html_w;
  end if;
end if;

return	ds_cor_grafico_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
