CREATE OR REPLACE FUNCTION obter_cor_grid_agenda_consig ( nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255)	:= null;


BEGIN

if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	SELECT MAX(SUBSTR(obter_cor_grid_planeja_consig(null,nr_sequencia,'L',NULL),1,255))
	into STRICT   ds_retorno_w
	FROM   agenda_pac_consignado a
	WHERE  a.nr_seq_agenda = nr_seq_agenda_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
