CREATE OR REPLACE FUNCTION obter_cor_grid_montagem ( nr_seq_agenda_p bigint, ie_linha_coluna_p text, ie_coluna_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(255)	:= null;
ds_cor_w	varchar(255)	:= null;

c00 CURSOR FOR
	SELECT	ds_cor
	from (SELECT	a.ds_cor,
			b.dt_atualizacao
		from	regra_cor_monta_cc a,
			agenda_regra_cor_monta_cc b
		where	a.nr_sequencia		= b.nr_seq_status
		and	b.nr_seq_agenda		= nr_seq_agenda_p
		and	b.ie_linha_coluna	= ie_linha_coluna_p
		and	coalesce(b.ie_coluna,'XPTO')	= coalesce(ie_coluna_p,'XPTO')
		and	coalesce(a.ie_situacao,'A')	= 'A'
		
union

		select	'clWhite' ds_cor,
			dt_atualizacao
		from	agenda_regra_cor_monta_cc
		where	coalesce(nr_seq_status::text, '') = ''
		and	nr_seq_agenda	= nr_seq_agenda_p
		and	ie_linha_coluna	= ie_linha_coluna_p
		and	coalesce(ie_coluna,'XPTO')	= coalesce(ie_coluna_p,'XPTO')) alias6
	order by dt_atualizacao;


BEGIN

open c00;
loop
fetch c00 into
	ds_cor_w;
EXIT WHEN NOT FOUND; /* apply on c00 */
	ds_retorno_w	:= ds_cor_w;
end loop;
close c00;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
