CREATE OR REPLACE FUNCTION obter_cor_interacao_medic ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cor_inte_w   	varchar(50);
ie_severidade_w		varchar(50);


BEGIN

select	max(ie_severidade)
into STRICT	ie_severidade_w
from	prescr_medica_interacao
where	nr_prescricao	= nr_prescricao_p;

select	max(ds_cor)
into STRICT	ds_cor_inte_w
from	rep_cor_interacao
where	ie_severidade	= ie_severidade_w;

return	ds_cor_inte_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
