CREATE OR REPLACE FUNCTION obter_cor_legenda_item_nf ( cd_material_p material.cd_material%Type, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


-- ie_retorno_p	CF - Cor Fundo
--		CL - Cor Legenda
cd_classe_material_w		CLASSE_MATERIAL.CD_CLASSE_MATERIAL%Type;
nr_seq_familia_w		MATERIAL_FAMILIA.NR_SEQUENCIA%Type;
nr_seq_localizacao_w		MATERIAL_LOCAL.NR_SEQUENCIA%Type;
ds_cor_fundo_w			regra_legenda_item_nf.DS_COR_FUNDO%Type := '0';
DS_COR_FONTE_w			regra_legenda_item_nf.DS_COR_FONTE%Type := '0';
ds_retorno_w			regra_legenda_item_nf.DS_COR_FUNDO%Type := '0';

C01 CURSOR FOR
	SELECT 	nr_sequencia,
		ds_cor_fundo,
		DS_COR_FONTE,
		cd_classe_material,
		nr_seq_familia,
		nr_seq_localizacao
	from   	regra_legenda_item_nf
	where (cd_classe_material = cd_classe_material_w or coalesce(cd_classe_material,'0') = '0' )
	and (nr_seq_familia = nr_seq_familia_w or coalesce(nr_seq_familia,'0') = '0' )
	and (nr_seq_localizacao = nr_seq_localizacao_w or coalesce(nr_seq_localizacao,'0') = '0' )
	order  	by 	nr_seq_localizacao,
			nr_seq_familia,
			cd_classe_material;

vetRegra	C01%RowType;


BEGIN

select	cd_classe_material,
	nr_seq_familia,
	nr_seq_localizacao
into STRICT	cd_classe_material_w,
	nr_seq_familia_w,
	nr_seq_localizacao_w
from	material
where	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	vetRegra;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if	((coalesce(ds_cor_fundo_w,'0') = '0') and (coalesce(DS_COR_FONTE_w,'0') = '0')) then
		if (coalesce(vetRegra.nr_seq_localizacao,'0') <> '0') then
			ds_cor_fundo_w := 	vetRegra.ds_cor_fundo;
			DS_COR_FONTE_w := 	vetRegra.DS_COR_FONTE;
		elsif (coalesce(vetRegra.nr_seq_familia,'0') <> '0') then
			ds_cor_fundo_w := 	vetRegra.ds_cor_fundo;
			DS_COR_FONTE_w := 	vetRegra.DS_COR_FONTE;
		elsif (coalesce(vetRegra.cd_classe_material,'0') <> '0') then
			ds_cor_fundo_w := 	vetRegra.ds_cor_fundo;
			DS_COR_FONTE_w := 	vetRegra.DS_COR_FONTE;
		end if;
	end if;
	end;
end loop;
close C01;

if (ie_retorno_p = 'CF') then
	ds_retorno_w := ds_cor_fundo_w;
elsif (ie_retorno_p = 'CL')	then
	ds_retorno_w := DS_COR_FONTE_w;
end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
