CREATE OR REPLACE FUNCTION obter_cor_leito_mapa ( cd_perfil_p regra_perm_panorama.cd_perfil%type, cd_classif_setor_p regra_panorama_cor_leito.cd_classif_setor%type, nr_atendimento_p atendimento_paciente.nr_atendimento%type, ie_html_p text default null) RETURNS varchar AS $body$
DECLARE


nr_seq_regra_w		regra_perm_panorama.nr_sequencia%type;
cd_convenio_w		atend_categoria_convenio.cd_convenio%type;
qt_horas_com_acomod_w	bigint;
qt_horas_total_w	bigint;
ds_retorno_w		varchar(255);

C01 CURSOR FOR
	SELECT	CASE WHEN ie_html_p='S' THEN ds_cor_html  ELSE ds_cor_fundo END  ds_cor
	into STRICT	ds_retorno_w
	from	regra_panorama_cor_leito
	where	nr_seq_regra = nr_seq_regra_w
	and	coalesce(cd_classif_setor, cd_classif_setor_p) = cd_classif_setor_p
	and	coalesce(cd_convenio, cd_convenio_w) = cd_convenio_w
	and	((coalesce(qt_horas,0) = 0) or (ie_desconsiderar_sem_acomod = 'S' and qt_horas_com_acomod_w > qt_horas) or (ie_desconsiderar_sem_acomod = 'N' and qt_horas_total_w > qt_horas))
	order by coalesce(cd_classif_setor,0), coalesce(cd_convenio,0), coalesce(qt_horas,0);


BEGIN

select	max(a.nr_sequencia)
into STRICT 	nr_seq_regra_w
from	regra_perm_panorama a
where	a.cd_perfil = cd_perfil_p
and exists (	SELECT 1
		from	regra_panorama_cor_leito x
		where	x.nr_seq_regra = a.nr_sequencia);

if (coalesce(nr_seq_regra_w::text, '') = '') then -- se n√£o encontrou a regra para o perfil, busca uma regra sem perfil definido.
	select	max(a.nr_sequencia)
	into STRICT 	nr_seq_regra_w
	from	regra_perm_panorama a
	where	coalesce(a.cd_perfil::text, '') = ''
	and exists (	SELECT 1
			from	regra_panorama_cor_leito x
			where	x.nr_seq_regra = a.nr_sequencia);
end if;

if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
	cd_convenio_w	:= obter_convenio_atendimento(nr_atendimento_p);

	select	trunc(sum((coalesce(a.dt_saida_unidade, clock_timestamp()) - a.dt_entrada_unidade)*24)) qt_horas
	into STRICT	qt_horas_total_w
	from 	atend_paciente_unidade a,
		setor_atendimento s,
		tipo_acomodacao t
	where	s.cd_setor_atendimento = a.cd_setor_atendimento
	and	t.cd_tipo_acomodacao = a.cd_tipo_acomodacao
	and	a.nr_atendimento = nr_atendimento_p
	and	s.cd_classif_setor = cd_classif_setor_p;

	select	trunc(sum((coalesce(a.dt_saida_unidade, clock_timestamp()) - a.dt_entrada_unidade)*24)) qt_horas
	into STRICT	qt_horas_com_acomod_w
	from 	atend_paciente_unidade a,
		setor_atendimento s,
		tipo_acomodacao t
	where	s.cd_setor_atendimento = a.cd_setor_atendimento
	and	t.cd_tipo_acomodacao = a.cd_tipo_acomodacao
	and	a.nr_atendimento = nr_atendimento_p
	and	coalesce(t.ie_sem_acomodacao,'N') = 'N'
	and	s.cd_classif_setor = cd_classif_setor_p;

	open C01;
	loop
	fetch C01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
