SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_local_pa (nr_seq_local_pa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cor_w		varchar(50);


BEGIN


select  max(ds_cor)
into STRICT	ds_cor_w
from   	pa_local
where	nr_sequencia = nr_seq_local_pa_p;

if	coalesce(ds_cor_w::text, '') = '' then

	select 	max(b.ds_cor)
	into STRICT	ds_cor_w
	from   	pa_local a,
		pa_grupo_local b
	where  	a.nr_seq_grupo_pa = b.nr_sequencia
	and	a.nr_sequencia = nr_seq_local_pa_p;

end if;

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
