CREATE OR REPLACE FUNCTION obter_cor_maior_etapa ( nr_interno_conta_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_etapa_w	bigint;
ds_cor_etapa_w	varchar(15) := '0';


BEGIN

begin
select	somente_numero(obter_dados_maior_etapa_conta(nr_interno_conta_p,'S'))
into STRICT	nr_seq_etapa_w
;
exception
when others then
	nr_seq_etapa_w := 0;
end;

begin
select	max(coalesce(ds_cor_etapa,0))
into STRICT	ds_cor_etapa_w
from	fatur_etapa
where	nr_sequencia	= nr_seq_etapa_w
and	coalesce(ie_situacao,'A') = 'A';
exception
when others then
	ds_cor_etapa_w := '0';
end;

return	ds_cor_etapa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
