CREATE OR REPLACE FUNCTION obter_cor_maior_seq_etapa ( nr_interno_conta_p bigint ) RETURNS varchar AS $body$
DECLARE


nr_seq_etapa_w	bigint;
ds_cor_etapa_w	varchar(15) := '0';

C01 CURSOR FOR
	SELECT	coalesce(a.nr_seq_etapa,0)
	from	fatur_etapa		b,
		conta_paciente_etapa	a
	where	a.nr_seq_etapa		= b.nr_sequencia
	and	a.nr_interno_conta	= nr_interno_conta_p
	and	coalesce(b.ie_situacao,'A')	= 'A'
	order by b.nr_seq_etapa;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_etapa_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_etapa_w:= nr_seq_etapa_w;
	end;
end loop;
close C01;

if (nr_seq_etapa_w IS NOT NULL AND nr_seq_etapa_w::text <> '') and (nr_seq_etapa_w	> 0) then

	select	max(coalesce(ds_cor_etapa,0))
	into STRICT	ds_cor_etapa_w
	from	fatur_etapa
	where	nr_sequencia	= nr_seq_etapa_w
	and	coalesce(ie_situacao,'A') = 'A';
end if;

if (coalesce(ds_cor_etapa_w::text, '') = '') then
	ds_cor_etapa_w	:=	0;
end if;

return	ds_cor_etapa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
