SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_maquina_senha (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_fonte_w			varchar(255);
ds_cor_fundo_w			varchar(255);
ds_maquina_w			varchar(255);
ds_retorno_w			varchar(255);

/*IE_OPCAO_P
W = Fonte
X = Fundo*/
BEGIN

select	max(DS_MAQUINA_CHAMADA)
into STRICT	ds_maquina_w
from	paciente_senha_fila
where	nr_sequencia = nr_sequencia_p;


select	max(coalesce(a.ds_cor_fonte,'')),
		max(coalesce(a.ds_cor_fundo,''))
into STRICT	ds_cor_fonte_w,
		ds_cor_fundo_w
from	maquina_local_senha a,
		computador b
where	a.nr_seq_computador	= b.nr_sequencia
and	nm_computador_pesquisa 	= padronizar_nome(upper(ds_maquina_w))
and		coalesce(a.ie_situacao, 'A') = 'A';

if (ie_opcao_p = 'W') then
	ds_retorno_w	:= ds_cor_fonte_w;
else
	ds_retorno_w	:= ds_cor_fundo_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_cor_maquina_senha (nr_sequencia_p bigint, ie_opcao_p text) FROM PUBLIC;

