SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_nivel_alta ( dt_entada_p timestamp, dt_prev_alta_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_cor_w  varchar(255) := '';
qt_porcentagem_w double precision;

BEGIN
begin
/*Converte para porcentagem*/

if (dt_prev_alta_p < clock_timestamp()) then
	qt_porcentagem_w := 100;
end if;
if (dt_prev_alta_p > clock_timestamp()) then
 qt_porcentagem_w := (100 - trunc(dividir((round(dt_prev_alta_p - clock_timestamp())*100),round(dt_prev_alta_p - dt_entada_p)),2));
end if;

if (qt_porcentagem_w = 0) then
	qt_porcentagem_w := 1;
end if;

select 	max(DS_COR)
into STRICT 	ds_cor_w
from 	REGRA_COR_PREV_ALTA
where 	qt_porcentagem_w between PR_INICIAL and PR_FINAL;
exception
	when others then
	null;
end;

return ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
