CREATE OR REPLACE FUNCTION obter_cor_nr_seq_local_pa ( NR_ATENDIMENTO_P bigint) RETURNS varchar AS $body$
DECLARE

NR_SEQ_LOCAL_PA_W numeric(20) := 0;
COR_LOCAL_PA_W	 varchar(20) := '';


BEGIN 
 
 
IF (coalesce(NR_ATENDIMENTO_P,0) > 0) THEN 
	BEGIN 
  SELECT DS_COR 
   INTO STRICT COR_LOCAL_PA_W 
   FROM PA_LOCAL A, 
      ATENDIMENTO_PACIENTE B 
   WHERE B.NR_ATENDIMENTO = NR_ATENDIMENTO_P 
    AND A.NR_SEQUENCIA = B.NR_SEQ_LOCAL_PA;
  END;
END IF;
 
RETURN COR_LOCAL_PA_W;
 
END;
