SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_nut_escala (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE



ds_cor_w		varchar(15);
qt_ponto_min_w		smallint;
qt_ponto_max_w		smallint;
nr_ult_seq_escala_w	bigint;
qt_pontuacao_escala_w	smallint;
cd_setor_atendimento_w	bigint;
ie_complexidade_nut_w	varchar(5);


BEGIN
cd_setor_atendimento_w	:= substr(obter_setor_atendimento(nr_atendimento_p),1,10);

select	max(ie_complexidade_nut)
into STRICT	ie_complexidade_nut_w
from	setor_atendimento
where	cd_setor_atendimento = cd_setor_atendimento_w;

if (coalesce(ie_complexidade_nut_w,'N') = 'N') then
	select 	MAX(nr_sequencia)
	into STRICT	nr_ult_seq_escala_w
	from   	escala_nrs
	where  	nr_atendimento = nr_atendimento_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_inativacao::text, '') = '';

	if (nr_ult_seq_escala_w IS NOT NULL AND nr_ult_seq_escala_w::text <> '') then

		select	max(qt_pontuacao)
		into STRICT	qt_pontuacao_escala_w
		from	escala_nrs
		where	nr_sequencia	= nr_ult_seq_escala_w;


		select	MAX(ds_cor)
		into STRICT	ds_cor_w
		from	regra_nut_escala
		where	qt_pontuacao_escala_w between qt_ponto_min and qt_ponto_max
		and	ie_complexidade_nut = 'N';

	end if;
elsif (coalesce(ie_complexidade_nut_w,'N') = 'M') then
	select 	max(nr_sequencia)
	into STRICT	nr_ult_seq_escala_w
	from   	escala_mst
	where  	nr_atendimento = nr_atendimento_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_inativacao::text, '') = '';

	if (nr_ult_seq_escala_w IS NOT NULL AND nr_ult_seq_escala_w::text <> '') then

		select	max(qt_pontuacao)
		into STRICT	qt_pontuacao_escala_w
		from	escala_mst
		where	nr_sequencia	= nr_ult_seq_escala_w;


		select	MAX(ds_cor)
		into STRICT	ds_cor_w
		from	regra_nut_escala
		where	qt_pontuacao_escala_w between qt_ponto_min and qt_ponto_max
		and	ie_complexidade_nut = 'M';

	end if;
elsif (coalesce(ie_complexidade_nut_w,'N') = 'S') then
	select 	max(nr_sequencia)
	into STRICT	nr_ult_seq_escala_w
	from   	escala_strong_kids
	where  	nr_atendimento = nr_atendimento_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_inativacao::text, '') = '';

	if (nr_ult_seq_escala_w IS NOT NULL AND nr_ult_seq_escala_w::text <> '') then

		select	max(qt_ponto)
		into STRICT	qt_pontuacao_escala_w
		from	escala_strong_kids
		where	nr_sequencia	= nr_ult_seq_escala_w;

		select	MAX(ds_cor)
		into STRICT	ds_cor_w
		from	regra_nut_escala
		where	qt_pontuacao_escala_w between qt_ponto_min and qt_ponto_max
		and	ie_complexidade_nut = 'S';

	end if;
end if;

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_cor_nut_escala (nr_atendimento_p bigint) FROM PUBLIC;

