CREATE OR REPLACE FUNCTION obter_cor_plano (CD_CONVENIO_P bigint, CD_PLANO_P text) RETURNS varchar AS $body$
DECLARE


COR_PLANO_W				varchar(15);


BEGIN

SELECT 	MAX(DS_COR)
INTO STRICT	COR_PLANO_W
FROM 	CONVENIO_PLANO
WHERE 	CD_CONVENIO = CD_CONVENIO_P 
AND 	CD_PLANO = CD_PLANO_P;

RETURN	COR_PLANO_W;	

END;
$body$
