CREATE OR REPLACE FUNCTION obter_cor_preca_ocupacao (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cor_w			varchar(20);
nr_sequencia_w			bigint;
nr_seq_atend_precaucao_w	bigint;

BEGIN

ds_cor_w := '';

if (coalesce(nr_atendimento_p,0) > 0) then

	select	coalesce(max(nr_Sequencia),0)
	into STRICT	nr_seq_atend_precaucao_w
	from	atendimento_precaucao
	where	nr_atendimento = nr_atendimento_p;


	if (nr_seq_atend_precaucao_w > 0) then

		select  nr_seq_precaucao
		into STRICT	nr_sequencia_w
		from	atendimento_precaucao
		where	nr_sequencia = nr_seq_atend_precaucao_w;

		select	max(ds_cor_ocupacao)
		into STRICT	ds_cor_w
		from	cih_precaucao
		where	nr_sequencia = nr_sequencia_w;
	end if;

end if;

return	ds_cor_w;

end;
$body$
