SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_procedencia_agenda (cd_procedencia_p bigint, ie_cor_p text) RETURNS varchar AS $body$
DECLARE


ds_cor_w	varchar(10);
ds_cor_fundo_w	varchar(10);
ds_cor_fonte_w 	varchar(10);


BEGIN

if (cd_procedencia_p > 0) then

	select	max(ds_cor_fundo),
		max(ds_cor_fonte)
	into STRICT	ds_cor_fundo_w,
		ds_cor_fonte_w
	from	procedencia
	where 	cd_procedencia	= cd_procedencia_p;

	if (ie_cor_p = 'B') then
		ds_cor_w := ds_cor_fundo_w;
	elsif (ie_cor_p = 'F') then
		ds_cor_w := ds_cor_fonte_w;
	end if;

end if;

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
