SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_queixa_paciente ( nr_atendimento_p bigint, nr_seq_queixa_p bigint) RETURNS varchar AS $body$
DECLARE


ds_cor_w		varchar(50);
nr_seq_queixa_w		bigint;


BEGIN

nr_seq_queixa_w := nr_seq_queixa_p;

if (coalesce(nr_seq_queixa_p::text, '') = '') then
	select	max(a.nr_seq_queixa)
	into STRICT	nr_seq_queixa_w
	from	ATEND_PACIENTE_QUEIXA a
	where	a.nr_atendimento	= nr_atendimento_p
	and	a.dt_atualizacao_nrec = (SELECT max(b.dt_atualizacao_nrec) from atend_paciente_queixa b where b.nr_atendimento = nr_atendimento_p);

	if (coalesce(nr_seq_queixa_w::text, '') = '') then
		select	max(nr_seq_queixa)
		into STRICT	nr_seq_queixa_w
		from	triagem_pronto_atend
		where	nr_atendimento = nr_atendimento_p;
	end if;
end if;


select	max(ds_cor)
into STRICT	ds_cor_w
from	QUEIXA_PACIENTE
where	nr_sequencia = nr_seq_queixa_w
and 	coalesce(cd_estabelecimento,wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
