CREATE OR REPLACE FUNCTION obter_cor_regra_gedipa (cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE

ds_cor_w	varchar(15);

BEGIN

ds_cor_w := 'clWhite';

if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
	select 	coalesce(max(ds_cor),'clWhite')
	into STRICT	ds_cor_w
	from 	intervalo_prescricao
	where 	cd_intervalo = cd_intervalo_p;
end if;

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
