SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_regra_meta_grupo ( nr_seq_grupo_des_p bigint, nm_usuario_p text, qt_atual_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
COR	= Cor referente a regra
PER	= Percentual de diferenÃ§a
INI	= Quantidade inicio dia
ATU	= Quantidade Atual*/
nr_sequencia_w		bigint;
nr_seq_grupo_w		bigint;
ds_retorno_w		varchar(255);
ds_cor_w			varchar(255);
qt_ordem_inicio_dia_w	integer;
qt_ordem_servico_w	integer;
pr_diferenca_w		real;
pr_diferenca_ww		real;



BEGIN

qt_ordem_servico_w	:= qt_atual_p;

if (nr_seq_grupo_des_p > 0) then
	nr_seq_grupo_w	:= nr_seq_grupo_des_p;
else
	nr_seq_grupo_w	:= obter_grupo_usuario_wheb(nm_usuario_p);
end if;

select	coalesce(sum(qt_ordem_servico), 0)
into STRICT	qt_ordem_inicio_dia_w
from	man_regra_resumo_meta
where	dt_referencia = OBTER_INICIO_SEMANA(trunc(clock_timestamp(), 'dd'))
and	nr_seq_grupo_des = nr_seq_grupo_w;

pr_diferenca_w		:= obter_variacao_entre_valor(qt_ordem_inicio_dia_w, qt_ordem_servico_w);
pr_diferenca_ww		:= null;
if (pr_diferenca_w < 0) then
	pr_diferenca_ww	:= abs(pr_diferenca_w);
end if;


select	coalesce(max(nr_sequencia), 0)
into STRICT	nr_sequencia_w
from	man_regra_cor_meta
where	pr_diferenca_w between coalesce(pr_diferenca_min, 0) and coalesce(pr_diferenca_max, 99999);

if (nr_sequencia_w > 0) then
	select	ds_cor
	into STRICT	ds_cor_w
	from	man_regra_cor_meta
	where	nr_sequencia	= nr_sequencia_w;
end if;

if (ie_opcao_p = 'COR') then
	ds_retorno_w	:= ds_cor_w;
elsif (ie_opcao_p = 'PER') then
	ds_retorno_w	:= coalesce(pr_diferenca_ww, pr_diferenca_w);
elsif (ie_opcao_p = 'INI') then
	ds_retorno_w	:= qt_ordem_inicio_dia_w;
elsif (ie_opcao_p = 'ATU') then
	ds_retorno_w	:= qt_ordem_servico_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
