CREATE OR REPLACE FUNCTION obter_cor_regra_observacao_pa ( dt_inicio_observacao_p timestamp, dt_fim_observacao_p timestamp, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
Cor		C
Cor da fonte		F
