*/
ds_cor_w			varchar(15) 	:= Null;
ds_cor_fonte_w			varchar(15)	:= Null;
ds_cor_resultado_w		varchar(15) 	:= Null;
ds_cor_fonte_resultado_w	varchar(15)	:= Null;
qt_observacao_w			bigint;
nr_seq_cor_w			bigint;
nr_seq_cor_resultado_w		bigint;

C01 CURSOR FOR
	SELECT	ds_cor,
		ds_cor_fonte,
		nr_sequencia
	from	regra_observacao_pa
	where	cd_estabelecimento = cd_estabelecimento_p
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p) = cd_setor_atendimento_p
	and	qt_observacao_w > qt_tempo
	order by  	coalesce(cd_setor_atendimento,0),
			coalesce(cd_estabelecimento,0);


BEGIN

If (dt_inicio_observacao_p IS NOT NULL AND dt_inicio_observacao_p::text <> '') then

	qt_observacao_w := round(((coalesce(dt_fim_observacao_p,clock_timestamp()) - dt_inicio_observacao_p)*24*60));

	If (qt_observacao_w > 0) then

		open C01;
		loop
		fetch C01 into
			ds_cor_w,
			ds_cor_fonte_w,
			nr_seq_cor_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ds_cor_resultado_w 		:= ds_cor_w;
