			nr_seq_cor_resultado_w 		:= nr_seq_cor_w;
			end;
		end loop;
		close C01;

	end if;

end if;

if (ie_opcao_p = 'C') then
	return	ds_cor_resultado_w;
elsif (ie_opcao_p = 'R') then
	return	nr_seq_cor_resultado_w;
else
	return	ds_cor_fonte_resultado_w;
end if;

end;
