CREATE OR REPLACE FUNCTION obter_cor_status_agecons (ie_status_agenda_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(25);


BEGIN

if (ie_opcao_p = 'FU') then
	Select 	max(DS_COR_FUNDO)
	into STRICT 	ds_retorno_w
	from 	AGENDA_CONS_STATUS_ADIC
	where 	ie_status_agenda = ie_status_agenda_p
	and	ie_situacao = 'A'
	and 	(DS_COR_FUNDO IS NOT NULL AND DS_COR_FUNDO::text <> '');

elsif (ie_opcao_p = 'FO') then
	Select 	max(DS_COR_FONTE)
	into STRICT 	ds_retorno_w
	from 	AGENDA_CONS_STATUS_ADIC
	where 	ie_status_agenda = ie_status_agenda_p
	and	ie_situacao = 'A'
	and 	(DS_COR_FONTE IS NOT NULL AND DS_COR_FONTE::text <> '');

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
