CREATE OR REPLACE FUNCTION obter_cor_status_dashboard_pmo ( nr_vl_dominio_p bigint ) RETURNS varchar AS $body$
DECLARE

/* ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Obter a cor relacionada ao status conforme o domínio 8175
---------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ]  Relatórios [ ] Outros:
 --------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
* A sequência de apresentação do cadastro de legendas 1611 é igual à sequência do dominio 8175

+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
pr_retorno_w varchar(50) := null;


BEGIN

  if (nr_vl_dominio_p IS NOT NULL AND nr_vl_dominio_p::text <> '') then
    select c.ds_cor_html
    into STRICT   pr_retorno_w
    from   tasy_padrao_cor c
    where  c.nr_seq_legenda = 1611
    and    c.nr_seq_apres   = nr_vl_dominio_p;
  end if;

  return  pr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_cor_status_dashboard_pmo ( nr_vl_dominio_p bigint ) FROM PUBLIC;

