CREATE OR REPLACE FUNCTION obter_cor_status_hemo_palm (ie_status_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_cor_w	bigint;
ds_cor_w	varchar(50)	:= '#FFFFFF';


BEGIN
if (ie_status_p = 'I')	then
	nr_seq_cor_w := 450;
elsif (ie_status_p = 'INT')	then
	nr_seq_cor_w := 451;
elsif (ie_status_p = 'R')	then
	nr_seq_cor_w := 452;
elsif (ie_status_p = 'T')	then
	nr_seq_cor_w := 453;
end if;

ds_cor_w :=  obter_tasy_obter_cor(nr_seq_cor_w, cd_perfil_p, cd_estabelecimento_p, 'F');
if (ie_status_p = 'N') or (coalesce(ie_status_p::text, '') = '') then
	ds_cor_w := '#FFFFFF';
elsif (upper(ds_cor_w) like('%MONEYGREEN%')) THEN
	ds_cor_w := '#66CDAA';
end if;

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
