CREATE OR REPLACE FUNCTION obter_cor_status_op (nr_seq_status_op_p bigint) RETURNS varchar AS $body$
DECLARE


/*
This function is used for Delphi and Java platforms
If you need to make any change to this function,
you'll gonna need to change the function "HTML_OBTER_COR_STATUS_OP" also,
that is used in HTML platform
*/
ds_retorno_w		varchar(15);

BEGIN
if (coalesce(nr_seq_status_op_p,0) > 0) then
	select	max(substr(lop.ds_cor,1,15))
	into STRICT	ds_retorno_w
	from 	status_legenda_op_opm slo,
		legenda_ordem_producao_opm lop
	where 	slo.nr_seq_status = nr_seq_status_op_p
	and 	slo.nr_seq_legenda = lop.nr_sequencia
	and 	coalesce(slo.ie_situacao,'A') = 'A'
	and 	coalesce(lop.ie_situacao,'A') = 'A';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
