SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_status_unidades ( ds_status_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, ie_html5_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_cor_status_w		varchar(15);
nr_seq_cor_w		bigint;
ds_cor_fonte     	varchar(15);
ds_cor_selecao      	varchar(15);
				

BEGIN

ds_cor_status_w := '';

	if ie_html5_p = 'N' then
		select	max(nr_sequencia)
		into STRICT	nr_seq_cor_w
		from	tasy_padrao_cor
		where	nr_seq_legenda = 1023
		and		upper(ds_item) = upper(ds_status_p);
	else
		select	max(nr_sequencia),
			max(ds_cor_html)
		into STRICT	nr_seq_cor_w,
			ds_cor_status_w
		from	tasy_padrao_cor
		where	nr_seq_legenda = 1023
		and		upper(obter_desc_expressao(cd_exp_item, '')) = upper(ds_status_p);
		
		
		
		if (ds_cor_status_w IS NOT NULL AND ds_cor_status_w::text <> '') then
			return ds_cor_status_w;
		end if;
		
	end if;
	if (coalesce(nr_seq_cor_w,0) > 0) then

		SELECT * FROM Tasy_Obter_Cor(nr_seq_cor_w, cd_perfil_p, cd_estabelecimento_p, ds_cor_status_w, ds_cor_fonte, ds_cor_selecao) INTO STRICT ds_cor_status_w, ds_cor_fonte, ds_cor_selecao;

	end if;

return	ds_cor_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
