CREATE OR REPLACE FUNCTION obter_cor_tasy ( nr_sequencia_p bigint, ie_opcao_p text, cd_perfil_p bigint default null, cd_estabelecimento_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15);
ds_cor_fundo_w		varchar(15);
ds_cor_fonte_w		varchar(15);
ds_cor_selecao_w		varchar(15);
cd_perfil_w		integer;
cd_estabelecimento_w	smallint;

/*	ie_opcao_p
	F - Cor de fundo
	T - Cor da fonte
	S - Cor da seleção	*/
BEGIN

if (coalesce(nr_sequencia_p,0) > 0) then
	begin
	cd_perfil_w		:= coalesce(cd_perfil_p,coalesce(wheb_usuario_pck.get_cd_perfil,0));
	cd_estabelecimento_w	:= coalesce(cd_estabelecimento_p,coalesce(wheb_usuario_pck.get_cd_estabelecimento,0));

	SELECT * FROM tasy_obter_cor(nr_sequencia_p, cd_perfil_w, cd_estabelecimento_w, ds_cor_fundo_w, ds_cor_fonte_w, ds_cor_selecao_w) INTO STRICT ds_cor_fundo_w, ds_cor_fonte_w, ds_cor_selecao_w;

	if (ie_opcao_p = 'F') then
		ds_retorno_w := ds_cor_fundo_w;
	elsif (ie_opcao_p = 'T') then
		ds_retorno_w := ds_cor_fonte_w;
	elsif (ie_opcao_p = 'S') then
		ds_retorno_w := ds_cor_selecao_w;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
