SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_tcr ( nr_sequencia_p bigint, ie_opcap_p text default null, ie_html5_p text default null) RETURNS varchar AS $body$
DECLARE


ds_cor_w	varchar(15);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (coalesce(ie_opcap_p,'C')	= 'C') then

		select	case when coalesce(ie_html5_p::text, '') = '' then ds_cor else ds_cor_html end
		into STRICT	ds_cor_w
		from	triagem_classif_risco
		where	nr_sequencia	= nr_sequencia_p;
	elsif (coalesce(ie_opcap_p,'C')	= 'F') then
		select	max(DS_COR_FONTE)
		into STRICT	ds_cor_w
		from	triagem_classif_risco
		where	nr_sequencia	= nr_sequencia_p;
	end if;

end if;

return ds_cor_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
