SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_cor_triagem ( nr_seq_fila_senha_p bigint, ie_tipo_cor_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_classif_w	triagem_pronto_atend.nr_seq_classif%type;
ds_cor_w		triagem_classif_risco.ds_cor%type;
tem_regra_tolife_w	varchar(1) := 'N';


BEGIN

select	max(NR_SEQ_CLASSIF)
into STRICT	nr_seq_classif_w
from	TRIAGEM_PRONTO_ATEND
where	nr_seq_fila_senha	=	nr_seq_fila_senha_p;

if (coalesce(nr_seq_classif_w::text, '') = '') then

	select	max(a.nr_seq_classif)
	into STRICT	nr_seq_classif_w
	from	triagem_pronto_atend a
	where	a.nr_sequencia		=
		(SELECT	max(b.nr_sequencia)
		from	triagem_pronto_atend b
		where	b.nr_atendimento		=
			(select	max(c.nr_atendimento)
			from	atendimento_paciente c
			where	c.nr_seq_pac_senha_fila	=	nr_seq_fila_senha_p));

end if;

-- Processo de classificacao de risco executado no sistema da ToLife - OS 982686
if (coalesce(nr_seq_classif_w::text, '') = '') then

	select	coalesce(max('S'), 'N')
	into STRICT	tem_regra_tolife_w
	from	TO_LIFE_TRIAGEM_RISCO LIMIT 1;

	if (tem_regra_tolife_w = 'S') then
	
		select	max(nr_seq_triagem)
		into STRICT	nr_seq_classif_w
		from	atendimento_paciente
		where	nr_seq_pac_senha_fila = nr_seq_fila_senha_p;
		
	end if;
	
end if;
-- Fim processo de classificacao de risco executado no sistema da ToLife - OS 982686
if (nr_seq_classif_w > 0) then
	if (upper(ie_tipo_cor_p) = 'FONTE') then
		select	max(DS_COR_FONTE)
		into STRICT	ds_cor_w
		from	TRIAGEM_CLASSIF_RISCO
		where	nr_sequencia = nr_seq_classif_w
		and	coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
		and	ie_situacao = 'A';
	elsif (uppeR(ie_tipo_cor_p) = 'FUNDO') then
		select	max(DS_COR)
		into STRICT	ds_cor_w
		from	TRIAGEM_CLASSIF_RISCO
		where	nr_sequencia = nr_seq_classif_w
		and	coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento
		and	ie_situacao = 'A';
	end if;
end if;

return	ds_cor_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_cor_triagem ( nr_seq_fila_senha_p bigint, ie_tipo_cor_p text) FROM PUBLIC;

