CREATE OR REPLACE FUNCTION obter_cotacao_item_solic ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint) RETURNS bigint AS $body$
DECLARE


nr_cot_compra_w		bigint;


BEGIN

select	max(nr_cot_compra)
into STRICT	nr_cot_compra_w
from (
	SELECT	max(a.nr_cot_compra) nr_cot_compra
	from	cot_compra a,
		cot_compra_item b
	where	a.nr_cot_compra = b.nr_cot_compra
	and	nr_solic_compra = nr_solic_compra_p
	and	nr_item_solic_compra = nr_item_solic_compra_p
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = ''
	
union all

	SELECT	max(a.nr_cot_compra) nr_cot_compra
	from	cot_compra a,
		cot_compra_item b,
		cot_compra_solic_agrup c
	where	a.nr_cot_compra = b.nr_cot_compra
	and	b.nr_cot_compra = c.nr_cot_compra
	and	b.nr_item_cot_compra = c.nr_item_cot_compra
	and	c.nr_solic_compra = nr_solic_compra_p
	and	c.nr_item_solic_compra = nr_item_solic_compra_p
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = '') alias5;

return	nr_cot_compra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
