CREATE OR REPLACE FUNCTION obter_cotacao_moeda_titulo (cd_moeda_p bigint, nr_titulo_receber_p bigint, tipo_titulo_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	double precision;


BEGIN
begin

	select	coalesce(vl_cotacao,1)
	into STRICT	ds_retorno_w
	from	moeda a,
		cotacao_moeda b
	where	a.cd_moeda 	= b.cd_moeda
	and	a.cd_moeda 	= cd_moeda_p
	and	b.dt_cotacao 	= (	SELECT	max(x.dt_cotacao)
	   			  	from	cotacao_moeda x
	   			  	where	x.cd_moeda = a.cd_moeda);
exception
when others then
	if (tipo_titulo_p = 'CR') then
		select VL_COTACAO
		into STRICT	ds_retorno_w
		from TITULO_RECEBER
		where NR_TITULO = nr_titulo_receber_p;
	else
		select VL_COTACAO
		into STRICT	ds_retorno_w
		from TITULO_PAGAR
		where NR_TITULO = nr_titulo_receber_p;
	end if;

	if (ds_retorno_w = 0 or coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := 1;
	end if;
end;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
