CREATE OR REPLACE FUNCTION obter_cotacao_moeda_ult ( cd_moeda_p COTACAO_MOEDA.CD_MOEDA%type, dt_cotacao_p COTACAO_MOEDA.DT_COTACAO%type ) RETURNS bigint AS $body$
DECLARE


vl_cotacao_w	COTACAO_MOEDA.VL_COTACAO%type;	


BEGIN

BEGIN
	SELECT	MAX(VL_COTACAO)
	INTO STRICT	vl_cotacao_w
	FROM	COTACAO_MOEDA
	WHERE	CD_MOEDA = cd_moeda_p
	AND	DT_COTACAO <= trunc(dt_cotacao_p) + 1
	ORDER BY DT_COTACAO DESC;
EXCEPTION
	WHEN no_data_found THEN vl_cotacao_w := 0;
END;

RETURN vl_cotacao_w;

END;
$body$
