CREATE OR REPLACE FUNCTION obter_cot_forn_observacao ( nr_seq_cot_forn_p bigint, ie_quebra_linha_p text) RETURNS varchar AS $body$
DECLARE


cd_material_w		integer;
ds_observacao_w		varchar(255);
Resultado_w	 	varchar(4000);
Quebra_w		varchar(10) 	:= chr(13) || chr(10);

c01 CURSOR FOR
SELECT	cd_material,
	ds_observacao
from	cot_compra_forn_item
where	nr_seq_cot_forn = nr_seq_cot_forn_p
and	coalesce(ie_situacao,'A') = 'A'
and	(ds_observacao IS NOT NULL AND ds_observacao::text <> '');


BEGIN

if (coalesce(ie_quebra_Linha_p,'N') <> 'S') then
	quebra_w		:= '  ';
end if;

OPEN C01;
LOOP
FETCH C01 into
	cd_material_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

		Resultado_w	:=	Resultado_w || cd_material_w || ' - ' || ds_observacao_w || ', ' || quebra_w;

	end;
END LOOP;

RETURN resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
