CREATE OR REPLACE FUNCTION obter_count_name_change ( cd_pessoa_fisica_p bigint , nr_sequencia_p bigint, dt_exportar_de_p timestamp, dt_exportar_ate_p timestamp) RETURNS varchar AS $body$
DECLARE

  ds_retorno_w varchar(2 ) := '01';
  nr_sequencia_w pessoa_fisica_alteracao.nr_sequencia%type;
  cd_pessoa_fisica_w pessoa_fisica_alteracao.cd_pessoa_fisica%type;
  loopcount smallint :=1;
  c01 CURSOR FOR
    SELECT   cd_pessoa_fisica ,
        nr_sequencia
      from pessoa_fisica_alteracao
      where cd_pessoa_fisica=cd_pessoa_fisica_p
      and ie_alteracao_nome ='S'
      and dt_aprovacao between dt_exportar_de_p and dt_exportar_ate_p;

BEGIN
  open c01;
  loop
    fetch c01
      into cd_pessoa_fisica_w ,
        nr_sequencia_w;
    EXIT WHEN NOT FOUND; /* apply on c01 */
    begin
      if ( nr_sequencia_w =nr_sequencia_p ) then
        exit;
      end if;
    end;
    loopcount:=loopcount+1;
  end loop;
close c01;
select   CASE WHEN  sign( loopcount-10 ) =-1 THEN  '0'|| loopcount  WHEN  sign( loopcount-10 ) =0 THEN  ''|| loopcount   ELSE ''|| loopcount END
  into STRICT ds_retorno_w
;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
