CREATE OR REPLACE FUNCTION obter_cpf_cnpj_pf_pj ( cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(80);


BEGIN
ds_retorno_w		:= '';
if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then
	begin
	select 	 cd_cgc
	into STRICT	 ds_retorno_w
	from	 pessoa_juridica
	where	 cd_cgc	= cd_cgc_p;
	end;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
	select   nr_cpf
	into STRICT	 ds_retorno_w
	from	 pessoa_fisica
	where	 cd_pessoa_fisica = cd_pessoa_fisica_p;
	end;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
