CREATE OR REPLACE FUNCTION obter_credito_nao_ident (nr_seq_cartao_baixa_p bigint, nr_seq_lote_cartao_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w    bigint;


BEGIN

if (nr_seq_cartao_baixa_p IS NOT NULL AND nr_seq_cartao_baixa_p::text <> '') then

        select  max(nr_seq_movto_pend)
        into STRICT    ds_retorno_w
        from    movto_banco_pend_baixa
        where   nr_seq_cartao_baixa     = nr_seq_cartao_baixa_p;

end if;
if (nr_seq_lote_cartao_p IS NOT NULL AND nr_seq_lote_cartao_p::text <> '') then

        select  max(nr_seq_movto_pend)
        into STRICT    ds_retorno_w
        from    movto_banco_pend_baixa
        where   nr_seq_lote_cartao      = nr_seq_lote_cartao_p;
end if;

return ds_retorno_w;

end;
$body$
