SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_crescimento_ano ( dt_ano_p timestamp, ie_origem_inf_p text) RETURNS bigint AS $body$
DECLARE


qt_contratos_w		double precision;
qt_contratos_atual_w	double precision;
qt_contratos_ant_w	double precision;
qt_resultado_w		double precision;
dt_primeiro_contrato_w	timestamp;


BEGIN

if (ie_origem_inf_p = 'CA') then
	select	count(*)
	into STRICT	qt_contratos_ant_w
	from	contrato
	where	(dt_inicio IS NOT NULL AND dt_inicio::text <> '')
	and	dt_inicio between pkg_date_utils.start_of((pkg_date_utils.start_of(dt_ano_p,'YEAR', 0)-1),'YEAR', 0) and (pkg_date_utils.start_of(dt_ano_p,'YEAR', 0)-1);

	select	count(*)
	into STRICT	qt_contratos_atual_w
	from	contrato
	where	(dt_inicio IS NOT NULL AND dt_inicio::text <> '')
	and	dt_inicio between pkg_date_utils.start_of(dt_ano_p,'YEAR', 0) and pkg_date_utils.add_month(pkg_date_utils.start_of(dt_ano_p,'YEAR', 0),12, 0)-1;

	qt_resultado_w	:= dividir((qt_contratos_atual_w - qt_contratos_ant_w), qt_contratos_ant_w) * 100;

	if (qt_contratos_ant_w = 0) then
		qt_resultado_w	:= qt_contratos_atual_w * 100;
	end if;

elsif (ie_origem_inf_p = 'CT') then

	select	min(c.dt_inicio)
	into STRICT	dt_primeiro_contrato_w
	from	com_canal b,
		com_canal_cliente a,
		com_cliente o,
		contrato c
	where	c.cd_cgc_contratado	= o.cd_cnpj
	and	a.nr_seq_cliente	= o.nr_sequencia
	and	b.nr_sequencia 		= a.nr_seq_canal
	and	o.ie_classificacao	= 'C'
	and	a.ie_tipo_atuacao	= 'V'
	and	coalesce(a.dt_fim_atuacao::text, '') = '';

	select	count(*)
	into STRICT	qt_contratos_ant_w
	from	com_canal b,
		com_canal_cliente a,
		com_cliente o,
		contrato c
	where	c.cd_cgc_contratado	= o.cd_cnpj
	and	a.nr_seq_cliente	= o.nr_sequencia
	and	b.nr_sequencia 		= a.nr_seq_canal
	and	o.ie_classificacao	= 'C'
	and	a.ie_tipo_atuacao	= 'V'
	and	dt_inicio between pkg_date_utils.start_of(dt_primeiro_contrato_w,'YEAR', 0) and (pkg_date_utils.start_of(dt_ano_p,'YEAR', 0)-1)
	and	coalesce(a.dt_fim_atuacao::text, '') = '';

	select	count(*)
	into STRICT	qt_contratos_atual_w
	from	com_canal b,
		com_canal_cliente a,
		com_cliente o,
		contrato c
	where	c.cd_cgc_contratado	= o.cd_cnpj
	and	a.nr_seq_cliente	= o.nr_sequencia
	and	b.nr_sequencia 		= a.nr_seq_canal
	and	o.ie_classificacao	= 'C'
	and	a.ie_tipo_atuacao	= 'V'
	and	dt_inicio between pkg_date_utils.start_of(dt_primeiro_contrato_w,'YEAR', 0) and pkg_date_utils.add_month(pkg_date_utils.start_of(dt_ano_p,'YEAR', 0),12, 0)-1
	and	coalesce(a.dt_fim_atuacao::text, '') = '';

	qt_resultado_w	:= abs(dividir((qt_contratos_atual_w - qt_contratos_ant_w), qt_contratos_ant_w) * 100);

	if (qt_contratos_ant_w = 0) then
		qt_resultado_w	:= qt_contratos_atual_w * 100;
	end if;
end if;

return qt_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
